.progress-container {
    width: 150px;
    height: 150px;
    background-color: #f3f3f3;
    border-radius: 50%;
    display: flex;
    justify-content: center;
    align-items: center;
    position: relative;
  }

  .progress {
    width: 100px;
    height: 100px;
    border-radius: 50%;
    clip-path: polygon(50% 0%, 100% 0%, 100% 100%);
    background-color: #3498db;
    position: absolute;
    transform: rotate(0deg);
  }

  .skills label{
    color: white;
    font-size: 20px;
  }
  .css{
    background-color: #0f4264;
  }
  .js{
    background-color: #3a627c;
  }
